import axios from "axios";
import { call, put, takeEvery } from "redux-saga/effects";

function PlayVideo(action) {
  return axios({
    method: "get",
    url: "http://localhost:5000/api/playvideo?filename="+action.payload,
    data: {},
  });
}

function* PlayVideoGenerator(action) {
  var result = yield call(PlayVideo, action);
  if (result.status == 200) {
    console.log(result);
    yield put({ type: "PLAY_VIDEO", payload: result.data });
  }
}

export function* PlayVideoSaga() {
  yield takeEvery("GET_PLAY_VIDEO", PlayVideoGenerator);
}
